import React from "react";
import { gql, useQuery } from "@apollo/client";

const GET_CHARACTER = gql`
    query GetCharacter($id: ID!){
        character (id: $id){
            id
            name
            image
        episode  {
        name
        episode
        }
    }
    }

`

export const useCharacter = (id) => {
    const {error,data,loading} = useQuery(GET_CHARACTER, {
        variables: {
            id
        }
    })

    return {
        error,
        data,
        loading
    }
}
